function getFamilyList(n){var t=$("#groupDropDown").val();$.ajax({url:"/Grupos/GetFamilyList/"+t,type:"POST",datatype:"application/json",contentType:"application/json",data:{},success:function(t){$("#familyDropDown").html("");$("#familyDropDown").append($("<option><\/option>").val(null).html("---Selecione uma Familia---"));t!==null&&$.each($.parseJSON(t),function(n,t){$("#familyDropDown").append($("<option><\/option>").val(t.IdFamilia).html(t.NomeFamilia))});$("#familyDropDown").val(n).change()}})}function getConcelhosList(n){var t=$("#distritoDropDown").val();$.ajax({url:"/Distritos/GetConcelhosList/"+t,type:"POST",datatype:"application/json",contentType:"application/json",data:{},success:function(t){$("#concelhoDropDown").html("");$("#concelhoDropDown").append($("<option><\/option>").val(null).html("---Selecione um Concelho---"));t!==null&&$.each($.parseJSON(t),function(n,t){$("#concelhoDropDown").append($("<option><\/option>").val(t.IdConcelho).html(t.NomeConcelho))});$("#concelhoDropDown").val(n).change()}})}